mapper.select:
SELECT hpl.*, l.customer_name, l.contact_phone FROM high_potential_lead hpl LEFT JOIN `lead` l ON hpl.lead_id = l.id WHERE 1=1
AND (hpl.user_id LIKE CONCAT('%', #{keyword}, '%') OR l.customer_name LIKE CONCAT('%', #{keyword}, '%'))
AND hpl.status = #{status}
ORDER BY hpl.matching_date DESC LIMIT #{offset}, #{size}

SELECT COUNT(*) FROM high_potential_lead hpl LEFT JOIN `lead` l ON hpl.lead_id = l.id WHERE 1=1
AND (hpl.user_id LIKE CONCAT('%', #{keyword}, '%') OR l.customer_name LIKE CONCAT('%', #{keyword}, '%'))
AND hpl.status = #{status}
