mapper.select:
SELECT * FROM `lead` WHERE deleted = 0
AND (customer_name LIKE CONCAT('%', #{keyword}, '%') OR contact_phone LIKE CONCAT('%', #{keyword}, '%') OR lead_no LIKE CONCAT('%', #{keyword}, '%'))
AND status = #{status}
ORDER BY create_time DESC LIMIT #{offset}, #{size}

SELECT COUNT(*) FROM `lead` WHERE deleted = 0
AND (customer_name LIKE CONCAT('%', #{keyword}, '%') OR contact_phone LIKE CONCAT('%', #{keyword}, '%') OR lead_no LIKE CONCAT('%', #{keyword}, '%'))
AND status = #{status}

SELECT * FROM `lead`
WHERE deleted = 0 AND lead_no = #{leadNo}
LIMIT 1
